mapper.select:
select 
  id,
  name,
  sku_id,
  max_buy_quantity,
  price,
  sort_order,
  description,
  begin_time,
  end_time,
  creator,
  ctime,
  modifier,
  mtime
from activity_discount


     id=#{id}


     and name=#{name}


     and sku_id=#{skuId}


     and max_buy_quantity=#{maxBuyQuantity}


     and price=#{price}


     and sort_order=#{sortOrder}


     and description=#{description}


     and begin_time=#{beginTime}


     and end_time=#{endTime}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}

