mapper.select:
select 
  id,
  name,
  sku_id,
  max_buy_quantity,
  quantity,
  settlement_price,
  price,
  sort_order,
  description,
  show_time,
  begin_time,
  end_time,
  is_white_list_activity,
  verify_status,
  creator,
  ctime,
  modifier,
  mtime
from activity_seckill


     id=#{id}


     and name=#{name}


     and sku_id=#{skuId}


     and max_buy_quantity=#{maxBuyQuantity}


     and quantity=#{quantity}


     and settlement_price=#{settlementPrice}


     and price=#{price}


     and sort_order=#{sortOrder}


     and description=#{description}


     and show_time=#{showTime}


     and begin_time=#{beginTime}


     and end_time=#{endTime}


     and is_white_list_activity=#{isWhiteListActivity}


     and verify_status=#{verifyStatus}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}

