mapper.select:
select 
  id,
  name,
  en_name,
  description,
  pic,
  sort_order,
  status,
  creator,
  ctime,
  modifier,
  mtime
from goods_brand


     id=#{id}


     and name=#{name}


     and en_name=#{enName}


     and description=#{description}


     and pic=#{pic}


     and sort_order=#{sortOrder}


     and status=#{status}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}

