mapper.select:
select 
  id,
  type,
  target_id,
  pic,
  sort_order,
  status,
  is_del,
  creator,
  ctime,
  modifier,
  mtime
from goods_file


     id=#{id}


     and type=#{type}


     and target_id=#{targetId}


     and pic=#{pic}


     and sort_order=#{sortOrder}


     and status=#{status}


     and is_del=#{isDel}


     and creator=#{creator}


     and ctime=#{ctime}


     and modifier=#{modifier}


     and mtime=#{mtime}

